import React, { useEffect, useState } from 'react';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { getUserProgress, updateUserProgress } from './api';
import ProgressChart from './components/ProgressChart';
import ProgressForm from './components/ProgressForm';

function App() {
  const [progressData, setProgressData] = useState([]);
  const userId = '_id'; // Replace with actual user ID, or fetch from user context or session

  useEffect(() => {
    getUserProgress(userId)
      .then(response => {
        setProgressData(response.data);
      })
      .catch(error => {
        console.error('Error fetching progress data:', error);
      });
  }, [userId]);

  const handleProgressUpdate = newWeight => {
    const updatedData = { currentWeight: newWeight };
    updateUserProgress(userId, updatedData)
      .then(response => {
        setProgressData(prevData => ({
          ...prevData,
          currentWeight: response.data.currentWeight
        }));
        alert('Progress updated successfully!');
      })
      .catch(error => {
        console.error('Failed to update progress:', error);
        alert('Failed to update progress.');
      });
  };

  const chartData = {
    labels: progressData.map(entry => entry.date), // Ensure 'date' is in your data
    datasets: [{
      label: 'Weight Progress',
      data: progressData.map(entry => entry.currentWeight),
      fill: false,
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  return (
    <Container>
      <Typography variant="h2" gutterBottom component="div">
        MacroMaster Progress Tracker
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <ProgressChart data={chartData} />
        </Grid>
        <Grid item xs={12} md={6}>
          <ProgressForm onSubmit={handleProgressUpdate} />
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
