import './App.css';
import axios from 'axios';
import { useState } from 'react';
import { TextField, Button, Table, TableBody, TableCell, TableHead, TableFooter, TableRow, Paper, Container, Typography } from '@mui/material';
import { Unstable_NumberInput as NumberInput } from '@mui/base/Unstable_NumberInput';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import weight from './API/weight.js';
import { createTheme } from '@mui/material/styles';
import { green, purple } from '@mui/material/colors';

function App() {

  const { palette } = createTheme();
  const { augmentColor } = palette;
  const createColor = (mainColor) => augmentColor({ color: { main: mainColor } });
  const theme = createTheme({
  palette: {
    teal: createColor('#1fe98b'),
    apple: createColor('#5DBA40'),
    steelBlue: createColor('#5C76B7'),
    violet: createColor('#BC00A3'),
  },
});
  const [activeTab, setActiveTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const [foodData, setFoodData] = useState([]);
  const [foodQuery, setFoodQuery] = useState('');

  const [actData, setActData] = useState([]);
  const [actQuery, setActQuery] = useState('');

  const [date, setDate] = useState('');
  const [cal, setCal] = useState('');
  const [protien, setProtien] = useState('');
  const [carb, setCarb] = useState('');
  const [fat, setFat] = useState('');


  const handleFoodChange = (e) => {
    setFoodQuery(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(cal + ' | ' + protien + ' | ' + carb + ' | ' + fat + ' | ' + date);
  }

  const handleFoodSearch = async () => {
    const API_KEY = "s6IZPc5umHSS4q56qzDw9d2YkF6BqO1G6rgefhty"

    try {
      const response = await axios.post(`https://api.nal.usda.gov/fdc/v1/foods/search?api_key=${API_KEY}`, {
        query: foodQuery,
      });
      const bestResponse = response.data.foods[0];
      var foods = [...foodData,{
        name: bestResponse.description,
        calories: bestResponse.foodNutrients.find(n => n.nutrientName === "Energy").value,
        protein: bestResponse.foodNutrients.find(n => n.nutrientName === "Protein").value,
        carbohydrates: bestResponse.foodNutrients.find(n => n.nutrientName === "Carbohydrate, by difference").value,
        fat: bestResponse.foodNutrients.find(n => n.nutrientName === "Total lipid (fat)").value,
      } ]
      setFoodData(foods);
    } catch (error) {
      console.error("Error fetching food data:", error);
    }
  };

  return (

    <Container maxWidth="lg">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            MacroMaster
          </Typography>
          {/* Placeholder for future login/logout functionality */}
        </Toolbar>
      </AppBar>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="Search food" />
        <Tab label="Entry Histroy" />
      </Tabs>
      {/* Placeholder for other tabs' content */}
      {activeTab === 0 && (
        <Container>
        <Typography variant="h6" gutterBottom style={{ marginTop: 20 }}>Search for Food Nutrition</Typography>
        <TextField fullWidth abel="Enter food name" value={foodQuery} onChange={handleFoodChange} style={{ marginBottom: 10 }}/>
        <Button variant="contained" color="secondary"  onClick={handleFoodSearch}>Search Food</Button>

        {foodData && (
          <Paper style={{ marginTop: 20 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell align="right">Calories</TableCell>
                  <TableCell align="right">Protein (g)</TableCell>
                  <TableCell align="right">Carbs (g)</TableCell>
                  <TableCell align="right">Fat (g)</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {foodData ? foodData.map((f) => {return (
                  <TableRow key={f.name}>
                  <TableCell component="th" scope="row"> {f.name} </TableCell>
                  <TableCell align="right">{f.calories}</TableCell>
                  <TableCell align="right">{f.protein}</TableCell>
                  <TableCell align="right">{f.carbohydrates}</TableCell>
                  <TableCell align="right">{f.fat}</TableCell>


                </TableRow>
                )}) : 
                <></>
              } 
              </TableBody>
              <TableFooter>
                <TableRow>
                  <TableCell>Totals</TableCell>
                  <TableCell align="right"> (g)</TableCell>
                  <TableCell align="right"> (g)</TableCell>
                  <TableCell align="right"> (g)</TableCell>
                  <TableCell align="right"> (g)</TableCell>
                </TableRow>
              </TableFooter>
            </Table>
          </Paper>
        )}
              <Container>

                <form id="macroForm" method="POST">
                  <input type="Date" id="date" name="Macro-Data" value = {date} onChange={ (e) => setDate(e.target.value)} />
                  <input type="number" id="cal" name = "email" value = {cal} onChange={ (e) => setCal(e.target.value)} placeholder="Calories (in grams)" min="0" max="100000" />
                  <input type="number" id="protien" value = {protien} onChange={ (e) => setProtien(e.target.value)}placeholder="Protien (in grams)" min="0" max="10000" />
                  <input type="number" id="carb" value = {carb} onChange={ (e) => setCarb(e.target.value)}placeholder="Carbs (in grams)" min="0" max="10000" />
                  <input type="number" id="fat" value = {fat} onChange={ (e) => setFat(e.target.value)}placeholder="Fat (in grams)" min="0" max="10000" />
                  <Button variant="contained" color="teal" type='submit' onClick={handleSubmit}>Submit</Button>


                </form>

              </Container>



      </Container>
      )}
      {activeTab === 1 && (
        <Container>

        </Container>
      )}

    <Container>

    </Container>

    </Container>

  );
}

export default App;
