import './App.css';
import { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@mui/material/Container';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';

function App() {

  const [activeTab, setActiveTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const useStyles = makeStyles((theme) => ({
    container: {
      backgroundColor: 'blue', // Change 'blue' to whatever color you desire
    },
  }));

  return (

    <Container maxWidth="lg">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            MacroMaster
          </Typography>
          {/* Placeholder for future login/logout functionality */}
        </Toolbar>
      </AppBar>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="Macro Entry" />
        <Tab label="Physical Activity" />
      </Tabs>
      {/* Placeholder for other tabs' content */}
      {activeTab === 0 && (
        <Typography>Enter Macros</Typography>
      )}
      {activeTab === 1 && (
        <Typography>Meal Builder</Typography>
      )}

    </Container>
  );
}

export default App;
